import React, { useState } from 'react';
import { Container, Row, Col, Card, Modal } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const Portfolio = () => {
  const [showModal, setShowModal] = useState(false);
  const [selectedWork, setSelectedWork] = useState(null);

  const works = [
    {
      id: 1,
      title: 'Project 1',
      image: 'project1.jpg',
      description: 'Description of Project 1...',
    },
    {
      id: 2,
      title: 'Project 2',
      image: 'project2.jpg',
      description: 'Description of Project 2...',
    },
    // Add more projects here
  ];

  const handleWorkClick = (work) => {
    setSelectedWork(work);
    setShowModal(true);
  };

  return (
    <Container className="py-5">
      <h2 className="text-center mb-4">My Portfolio</h2>
      <Row>
        {works.map((work) => (
          <Col key={work.id} md={4} sm={6} className="mb-4">
            <Card onClick={() => handleWorkClick(work)} className="portfolio-card">
              <Card.Img variant="top" src={work.image} alt={work.title} />
              <Card.Body>
                <Card.Title>{work.title}</Card.Title>
                <Card.Text>{work.description}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{selectedWork && selectedWork.title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedWork && (
            <>
              <img
                src={selectedWork.image}
                alt={selectedWork.title}
                className="img-fluid mb-3"
              />
              <p>{selectedWork.description}</p>
            </>
          )}
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default Portfolio;
